namespace App\Traits;

trait OseSVGTrait
{

    public function svg()
    {
        $name = strtolower($this->name);
        if (str_contains($name, 'debian')) {
            return $this->debian();
        }
        if (str_contains($name, 'ubuntu')) {
            return $this->ubuntu();
        }
        if (str_contains($name, 'centos')) {
            return $this->centos();
        }
        if (str_contains($name, 'redhat')) {
            return $this->redhat();
        }
        if (str_contains($name, 'windows')) {
            return $this->windows();
        }
        return $this->default();
    }

    private function default():string
    {
        return '<i class="bi bi-file-earmark-zip" style="font-size: 3rem"></i>';
    }

